scripts.draw:
function draw(){

    var size = chartwerk.ui.size;

    var VALUE_KEY = chartwerk.datamap.value;
    var UNIT_SPACING = 4;
    var UNITS_PER_ROW = size === 'double' ? 18 : 12;
    var CHART_WIDTH = size === 'double' ? 620 : 300;

    var unitSize = ((CHART_WIDTH) / UNITS_PER_ROW) - UNIT_SPACING;
    var totalUnits = chartwerk.data.reduce(function(m, d) {
        return m + d[VALUE_KEY];
    }, 0);

    var chartHeight = Math.ceil(totalUnits / UNITS_PER_ROW) * (UNIT_SPACING + unitSize);

    var werk = werkHelper.build({
        dims: {
          single: {
            width: CHART_WIDTH,
            height: chartHeight
          },
          double: {
            width: CHART_WIDTH,
            height: chartHeight
          }
        }
    });

    var svg = d3.select('#chart')
        .append('svg')
        .attr('width', werk.dims.svg.width + werk.dims.margins.left + werk.dims.margins.right)
        .attr('height', werk.dims.svg.height + werk.dims.margins.top + werk.dims.margins.bottom)
      .append('g')
        .attr('transform', 'translate(' + werk.dims.margins.left + ',' + werk.dims.margins.top + ')');

    var i = 0;
    var data = chartwerk.data.map(function(d) {
      return d3.range(d[VALUE_KEY]).map(function() {
        var e = JSON.parse(JSON.stringify(d));
        e.idx = i++;
        return e;
      });
    });

    var facets = svg.selectAll('.facet')
      .data(data)
      .enter().append('g')
      .attr('class', 'facet');

    facets.selectAll('.unit')
      .data(function(d) {
        return d;
      })
      .enter()
      .append('rect')
      .attr('class', 'unit')
      .attr('width', unitSize)
      .attr('height', unitSize)
      .attr('fill', function(d) {
        return werk.scales.color(d[chartwerk.datamap.scale]);
      })
      .attr('x', function(d) {
        return d.idx % UNITS_PER_ROW * (unitSize + UNIT_SPACING);
      })
      .attr('y', function(d) {
        return Math.floor(d.idx / UNITS_PER_ROW) * (unitSize + UNIT_SPACING);
      });

  facets
    .on('mouseover', function(d) {
      var highlighted = d3.select(this).classed('highlight', true);
      d3.select('.tooltip .title').text(d[0][chartwerk.datamap.scale]);
      d3.select('.tooltip .value').text(d[0][VALUE_KEY]);

      highlighted.selectAll('.unit')
        .attr('stroke', d3.rgb(werk.scales.color(d[0][chartwerk.datamap.scale])).darker())
        .attr('stroke-width', 2)

      tooltip.style('opacity', 1);
    })
    .on('mouseout', function(d) {
      d3.select(this)
        .classed('highlight', false)
        .selectAll('.unit')
          .attr('stroke-width', 0);

      tooltip.style('opacity', 0);
    })
    .on('mousemove', function(d) {
      var p = d3.mouse(this.parentElement.parentElement);
      tooltip
        .style("left", function(){
            // We position either left or right of the mouse point based
            // on whether we're past the midpoint of the chart. This protects
            // against tooltips overflowing embedded iframes.
            var s = chartwerk.ui.size,
                w = werk.dims[s].width,
                tipW = parseInt(d3.select(".tooltip").style("width"), 10),
                pos = p[0] > (w / 2) ?
                    p[0] - (tipW + 10) : p[0] + 15;
            return pos.toString() + "px";
        })
        .style("top", function(){
            var s = chartwerk.ui.size,
                h = werk.dims[s].height,
                tipH = parseInt(d3.select(".tooltip").style("height"), 10),
                pos = p[1] > (h / 2) ?
                    p[1] - (tipH + 5) : p[1] + 20;
            return pos.toString() + "px";
        });
    });


    var tooltip = d3.select("#chart")
      .append("div")
        .attr("class","tooltip")
        .style("position","absolute");
    tooltip
      .append("div")
      .attr("class","title")
      .text(chartwerk.datamap.scale);
    tooltip
      .append("div")
      .attr("class","value");
}

scripts.helper:
var werkHelper = {
    dims: function(werk){
        var s = chartwerk.ui.size;
            w = werk.dims[s].width,
            h = werk.dims[s].height,
            margins = {
                right: chartwerk.margins[s].right * w,
                left: chartwerk.margins[s].left * w,
                top: chartwerk.margins[s].top * h,
                bottom: chartwerk.margins[s].bottom * h
            },
            svg = {
                width: w - margins.left - margins.right,
                height: h + 2 - margins.top - margins.bottom
            };

        werk.dims.margins = margins;
        werk.dims.svg = svg;
    },

    scales: function(werk){
        var svg = werk.dims.svg;
        werk.scales = {
            color: chartwerk.axes.color.quantize ? 
                d3.scaleQuantize() : d3.scaleOrdinal(),
        };

        werk.scales.color
            .domain(chartwerk.axes.color.domain)
            .range(chartwerk.axes.color.range);
    },

    // Build dims, scales and axes.
    build: function(werk){
        this.dims(werk);
        this.scales(werk);
        return werk;
    },
};

scripts.styles:
#chartwerk {
    font-family:'Theinhardt-Regular',sans-serif; 
    width:620px;
}
#chartwerk.single{
    width:300px;
    max-width:100%;
    overflow:hidden;
    margin:0;
}
#chartwerk #headline {
    font-family:'Theinhardt-Medium',sans-serif; ;
    font-weight:normal;
    font-size:24px;
    line-height: 1.2;
}
#chartwerk #chatter {
    font-size:14px;
    line-height:19px;
    margin:5px 0 10px;
}
#chartwerk #footnote,
#chartwerk #source,
#chartwerk #author {
    font-size:11px;
    color:grey;
}
#chartwerk #author {
    text-align:right;
}
#chartwerk #chart{
    background-color:white;
    margin:3px;
}
#chartwerk #chart-legend, #chartwerk .chart-legend-container {
    font-family:'Theinhardt-Regular',sans-serif;
    font-size:14px;
    line-height:19px;
}
#chartwerk .chart-legend-container .title {
    margin-bottom:5px;
}
#chartwerk .chart-legend-container .key-label{
    font:11px 'Theinhardt-Regular',sans-serif;
}
#chartwerk .chart-legend-container .key-color{
    margin-right:2px;
}
#chartwerk .chart-legend-container .key{
    margin-right:8px;
}
#chartwerk .annotation p{
    font-family:'Theinhardt-Regular',sans-serif; 
}
#chartwerk #chart .tooltip{
    font: 13px 'Theinhardt-Regular',sans-serif; 
    opacity:0;
    pointer-events:none;
    background:rgba(255,255,255,.75);
    padding:5px;
    width:auto;
}
#chartwerk #chart .tooltip .title{
    font-family: 'Theinhardt-Medium',sans-serif;
    font-weight: normal;
    fill:grey;
}
